mapper.select:
SELECT
FROM COMPARE_SQL_COLUMN b LEFT JOIN (select * from COMPARE_SQL_RESULT t where not exists (select 1 from COMPARE_SQL_RESULT where ID_COMPARE_SQL_COLUMN=t.ID_COMPARE_SQL_COLUMN and ID_COMPARE_SQL_RESULT>t.ID_COMPARE_SQL_RESULT) ) a ON b.ID_COMPARE_SQL_COLUMN = a.ID_COMPARE_SQL_COLUMN LEFT JOIN COMPARE_SQL d ON d.ID_COMPARE_SQL = b.ID_COMPARE_SQL LEFT JOIN PROFILE_TABLE_GROUP e ON e.ID_PROFIEL_TABLE_GROUP = d.ID_COMPARE_TABLE_GROUP LEFT JOIN R_DATABASE f ON f.ID_DATABASE = d.ID_DATABASE
AND d.ID_COMPARE_TABLE_GROUP = #{compareTableGroup.profielTableGroupId}
AND d.ID_COMPARE_SQL = #{compareSql.compareSqlId}
order by a.ID_COMPARE_SQL_RESULT desc
limit ${start},${limit}

SELECT COUNT(*) FROM (select * from COMPARE_SQL_RESULT t where not exists (select 1 from COMPARE_SQL_RESULT where ID_COMPARE_SQL_COLUMN=t.ID_COMPARE_SQL_COLUMN and ID_COMPARE_SQL_RESULT>t.ID_COMPARE_SQL_RESULT) ) a
JOIN COMPARE_SQL_COLUMN b ON b.ID_COMPARE_SQL_COLUMN = a.ID_COMPARE_SQL_COLUMN JOIN COMPARE_SQL d ON d.ID_COMPARE_SQL = b.ID_COMPARE_SQL JOIN PROFILE_TABLE_GROUP e ON e.ID_PROFIEL_TABLE_GROUP = d.ID_COMPARE_TABLE_GROUP LEFT JOIN R_DATABASE f ON f.ID_DATABASE = d.ID_DATABASE
AND d.ID_COMPARE_TABLE_GROUP = #{compareTableGroup.profielTableGroupId}
