mapper.select:
select

from KDI_SYS_REPOSITORY_DATABASE a


    a.DEL_FLAG = 0

        AND  a.DATABASE_TYPE  = #{databaseType}


        AND  a.DATABASE_CONTYPE = #{databaseContype}

mapper.insert:
insert into KDI_SYS_REPOSITORY_DATABASE (
      REPOSITORY_CONNECTION_ID
    , REPOSITORY_CONNECTION_NAME
    , DATABASE_TYPE
    , DATABASE_CONTYPE
    , HOST_NAME
    , DATABASE_NAME
    , PORT
    , USERNAME
    , PASSWORD
    , SERVERNAME
    , DATA_TBS
    , INDEX_TBS
    , CREATE_USER_ID
    , UPDATE_USER_ID
    , CREATE_TIME
    , UPDATE_TIME
    , DEL_FLAG
)values(
      #{repositoryConnectionId}
    , #{repositoryConnectionName}
    , #{databaseType}
    , #{databaseContype}
    , #{hostName}
    , #{databaseName}
    , #{port}
    , #{username}
    , #{password}
    , #{servername}
    , #{dataTbs}
    , #{indexTbs}
    , #{createUserId}
    , #{updateUserId}
    , #{createTime}
    , #{updateTime}
    , #{delFlag}
)
