mapper.select:
select

from KDI_SYS_ROLE a


    a.DEL_FLAG = 0

        AND  a.DEPT_ID  = #{deptId}



        AND  a.ROLE_NAME like #{pattern}

select
from KDI_SYS_ROLE a
left join KDI_SYS_USER_ROLE ur on ur.ROLE_ID= a.ROLE_ID left join KDI_SYS_USER u on ur.USER_ID = u.USER_ID where a.DEL_FLAG = 0 and u.USER_ID = #{userId}
