mapper.insert:
insert into KDI_SYS_USER (
      ID_USER
    , USERNAME
    , PASSWORD
    , EMAIL
    , MOBILE
    , STATUS
    , ID_DEPT
    , CREATE_USER
    , UPDATE_USER
    , CREATE_TIME
    , UPDATE_TIME
    , DEL_FLAG
)values(
      #{userId}
    , #{username}
    , #{password}
    , #{email}
    , #{mobile}
    , #{status}
    , #{deptId}
    , #{createUser}
    , #{updateUser}
    , #{createTime}
    , #{updateTime}
    , #{delFlag}
)

mapper.select:
select
from KDI_SYS_USER a
left join KDI_SYS_USER_ROLE ur on ur.ID_USER= a.ID_USER left join KDI_SYS_ROLE r on ur.ROLE_ID = r.ROLE_ID where a.DEL_FLAG = 0 and r.ROLE_ID = #{roleId}

mapper.update:
update KDI_SYS_USER a

      a.ID_USER = #{userId}
    , a.USERNAME = #{username}
    , a.PASSWORD = #{password}
    , a.EMAIL = #{email}
    , a.MOBILE = #{mobile}
    , a.STATUS = #{status}
    , a.ID_DEPT = #{deptId}
    , a.UPDATE_USER = #{updateUser}
    , a.UPDATE_TIME = #{updateTime}
    , a.DEL_FLAG = #{delFlag}

where a.ID_USER = #{et.id}

UPDATE KDI_SYS_USER SET
LOGIN_IP = #{loginIp},
LOGIN_TIME = #{loginTime}
WHERE ID_USER = #{userId}
